// import Swiper core and required modules
import { Navigation, Pagination, Scrollbar, A11y } from 'swiper';

import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/scss';
import 'swiper/scss/navigation';
import 'swiper/scss/pagination';

import styles from './index.module.sass';
import {LayoutContainer} from "../../component/LayoutCointainer";

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

import ecomarket from "../../assets/img/ecomarket.svg";
import map from "../../assets/img/map.svg";
import {Icon} from "../../component/ui/Icon";

export const Home = () => {
    return (
        <LayoutContainer>
            <div className={ styles.slider }>
                <Swiper
                    // install Swiper modules
                    modules={[Navigation]}
                    spaceBetween={50}
                    slidesPerView={1}
                    navigation
                >
                    <SwiperSlide>
                        <div className={ styles.slide___text}>
                            <h1>
                                Сделаем мир чище
                            </h1>
                            <p>Сдай макулатуру или старую одежду и получи скидку  на покупку товаров из переработанных материалов</p>
                            <button>
                                Условия сервиса
                            </button>
                        </div>
                    </SwiperSlide>
                    <SwiperSlide>
                        <div className={ styles.slide___text}>
                            <h1>
                                А вы знали...
                            </h1>
                            <p>Сдай макулатуру или старую одежду и получи скидку  на покупку товаров из переработанных материалов</p>
                            <button>
                                Условия сервиса
                            </button>
                        </div>
                    </SwiperSlide>
                    <SwiperSlide>Slide 3</SwiperSlide>
                    <SwiperSlide>Slide 4</SwiperSlide>

                </Swiper>
            </div>


            <div className={ styles.additions }>
                <div className={ styles.additions__wrapper }>
                    <div className={ styles.additions__info }>
                        <h3>Пункты сбора</h3>
                        <p>Посмотри, где в твоем городе можно сдать вторсырье  на переработку</p>
                        <button>
                            <Icon name='next_additions' size = '48' />
                        </button>
                    </div>
                    <img className={ styles.additions__map } src={ map } alt="map"/>
                </div>
                <div className={ styles.additions__wrapper }>
                    <div className={ styles.additions__info }>
                        <h3>ЭкоМаркет</h3>
                        <p>Используй заработанные экокоины для покупки товаров из переработанных материалов </p>
                        <button>
                            <Icon name='next_additions' size = '48' />
                        </button>
                    </div>
                    <img className={ styles.additions__ecomarket } src={ ecomarket } alt="map"/>
                </div>
            </div>

        </LayoutContainer>
    )
};
